const express = require('express');
const Listing = require('../models/Listing');  // Ensure Listing model is using the correct collection
const router = express.Router();

// Route to get all listings from the 'listingsAndReviews' collection
router.get('/', async (req, res) => {
  try {
    const listings = await Listing.find();  // Fetch listings from MongoDB
    res.json(listings);  // Return listings in JSON format
  } catch (error) {
    res.status(500).json({ message: error.message });  // Handle error
  }
});

// Route to create a new listing
router.post('/', async (req, res) => {
  const { name, description, price, location, images, rating, reviews_count } = req.body;

  const newListing = new Listing({
    name,
    description,
    price,
    location,
    images,
    rating,
    reviews_count,
  });

  try {
    const savedListing = await newListing.save();  // Save the new listing to the database
    res.status(201).json(savedListing);  // Respond with the saved listing
  } catch (error) {
    res.status(400).json({ message: error.message });  // Handle error
  }
});

module.exports = router;
